mapper.update:
update order_inf
set food_name = #{foodName,jdbcType=VARCHAR},
  price = #{price,jdbcType=DOUBLE},
  num = #{num,jdbcType=INTEGER},
  order_id = #{orderId,jdbcType=BIGINT}
where id = #{id,jdbcType=BIGINT}

mapper.select:
select id, food_name, price, num, order_id
from order_inf
where id = #{id,jdbcType=BIGINT}

