mapper.update:
	update user
  set name = #{name,jdbcType=VARCHAR},
  location = #{location,jdbcType=VARCHAR},
    tel = #{tel,jdbcType=VARCHAR}
  where u_id = #{uId,jdbcType=BIGINT}

mapper.select:
select u_id, name, location, tel, create_time, update_time
from user
where u_id = #{uId,jdbcType=BIGINT}
